mapper.insert:
INSERT INTO aggregate_snapshot (
    aggregate_id, aggregate_type, version, snapshot_data
) VALUES (
    #{aggregateId}, #{aggregateType}, #{version}, #{snapshotData}::jsonb
)
ON CONFLICT (aggregate_id, version) DO NOTHING

mapper.delete:
DELETE FROM aggregate_snapshot
WHERE aggregate_id = #{aggregateId}
  AND version NOT IN (
      SELECT version
      FROM aggregate_snapshot
      WHERE aggregate_id = #{aggregateId}
      ORDER BY version DESC
      LIMIT #{keepCount}
  )
