mapper.select:
SELECT * FROM "実際原価データ"
WHERE "品目コード" = #{itemCode}
ORDER BY "作成日時" DESC

mapper.insert:
INSERT INTO "実際原価データ" (
    "作業指示番号", "品目コード", "完成数量",
    "実際材料費", "実際労務費", "実際経費", "実際製造原価", "単位原価"
) VALUES (
    #{workOrderNumber}, #{itemCode}, #{completedQuantity},
    #{actualMaterialCost}, #{actualLaborCost}, #{actualExpense}, #{actualManufacturingCost}, #{unitCost}
)
