mapper.select:
SELECT * FROM "在庫情報"
WHERE "場所コード" = #{locationCode}
  AND "品目コード" = #{itemCode}

SELECT * FROM "在庫情報"
WHERE "場所コード" = #{locationCode}
ORDER BY "品目コード"

SELECT * FROM "在庫情報"
WHERE "品目コード" = #{itemCode}
ORDER BY "場所コード"

mapper.update:
UPDATE "在庫情報"
SET "在庫数量" = "在庫数量" + #{quantity},


            "合格数" = "合格数" + #{quantity}


            "不良数" = "不良数" + #{quantity}


            "未検査数" = "未検査数" + #{quantity}

    ,
    "更新日時" = CURRENT_TIMESTAMP
WHERE "場所コード" = #{locationCode}
  AND "品目コード" = #{itemCode}

UPDATE "在庫情報"
SET "在庫数量" = "在庫数量" - #{quantity},


            "合格数" = "合格数" - #{quantity}


            "不良数" = "不良数" - #{quantity}


            "未検査数" = "未検査数" - #{quantity}

    ,
    "更新日時" = CURRENT_TIMESTAMP
WHERE "場所コード" = #{locationCode}
  AND "品目コード" = #{itemCode}

UPDATE "在庫情報"
SET


            "合格数" = "合格数" - #{quantity}


            "不良数" = "不良数" - #{quantity}


            "未検査数" = "未検査数" - #{quantity}

    ,


            "合格数" = "合格数" + #{quantity}


            "不良数" = "不良数" + #{quantity}


            "未検査数" = "未検査数" + #{quantity}

    ,
    "更新日時" = CURRENT_TIMESTAMP
WHERE "場所コード" = #{locationCode}
  AND "品目コード" = #{itemCode}
