mapper.select:
SELECT * FROM "工数実績データ"
WHERE "作業指示番号" = #{workOrderNumber}
ORDER BY "工順", "作業日"

SELECT COALESCE(SUM("工数"), 0)
FROM "工数実績データ"
WHERE "作業指示番号" = #{workOrderNumber} AND "工順" = #{sequence}

SELECT COALESCE(SUM("工数"), 0)
FROM "工数実績データ"
WHERE "作業指示番号" = #{workOrderNumber}

SELECT COALESCE(SUM("工数"), 0)
FROM "工数実績データ"
WHERE "担当者コード" = #{employeeCode}
  AND "作業日" BETWEEN #{startDate} AND #{endDate}
