mapper.update:
UPDATE 取引先分類所属マスタ
SET
    更新日時 = CURRENT_TIMESTAMP,
    更新者名 = #{updatedBy}
WHERE
    取引先分類種別コード = #{categoryTypeCode}
    AND 取引先分類コード = #{companyCategoryCode}
    AND 取引先コード = #{companyCode}

mapper.delete:
DELETE FROM 取引先分類所属マスタ
WHERE
    取引先分類種別コード = #{categoryTypeCode}
    AND 取引先分類コード = #{companyCategoryCode}
    AND 取引先コード = #{companyCode}
