mapper.update:
UPDATE 取引先分類マスタ
SET
    取引先分類名 = #{companyCategoryName},
    更新日時 = CURRENT_TIMESTAMP,
    更新者名 = #{updatedBy}
WHERE
    取引先分類種別コード = #{categoryTypeCode}
    AND 取引先分類コード = #{companyCategoryCode}

mapper.delete:
DELETE FROM 取引先分類マスタ
WHERE
    取引先分類種別コード = #{categoryTypeCode}
    AND 取引先分類コード = #{companyCategoryCode}
