mapper.update:
UPDATE 在庫データ
SET
    実在庫数 = #{actualQuantity},
    有効在庫数 = #{validQuantity},
    最終出荷日 = #{lastDeliveryDate},
    更新日時 = CURRENT_TIMESTAMP,
    更新者名 = #{updatedBy}
WHERE
    倉庫コード = #{warehouseCode}
    AND 商品コード = #{productCode}
    AND ロット番号 = #{lotNo}
    AND 在庫区分 = #{stockType}
    AND 良品区分 = #{qualityType}

mapper.delete:
DELETE FROM 在庫データ
WHERE
    倉庫コード = #{warehouseCode}
    AND 商品コード = #{productCode}
    AND ロット番号 = #{lotNo}
    AND 在庫区分 = #{stockType}
    AND 良品区分 = #{qualityType}

mapper.update:
UPDATE 在庫データ
SET
    有効在庫数 = #{validQuantity},
    更新日時 = CURRENT_TIMESTAMP
WHERE
    倉庫コード = #{warehouseCode}
    AND 商品コード = #{productCode}
    AND ロット番号 = #{lotNo}
    AND 在庫区分 = #{stockType}
    AND 良品区分 = #{qualityType}
